import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import '@vkontakte/vkui';
import { Input } from '@vkontakte/vkui';

//bridge.send("VKWebAppInit");

import Home from './panels/Home';
import Persik from './panels/Persik';



alert(bridge.send("VKWebAppInit"));

const App = () => {


	bridge.send("VKWebAppInit");
	bridge.send("VKWebAppGetClientVersion");

	const [scheme, setScheme] = useState('bright_light')
	const [activePanel, setActivePanel] = useState('home');
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(<ScreenSpinner size='large' />);

	useEffect(() => {
		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				setScheme(data.scheme)
			}
		});

		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
		<ConfigProvider scheme={scheme}>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						<SplitCol>
							<View activePanel={activePanel}>
								<Home id='home' fetchedUser={fetchedUser} go={go} />
								<Persik id='persik' go={go} />
							</View>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}









const textInput = React.createRef();
const clear = () => (textInput.current.value = "");

<View activePanel="input">
  <Panel id="input">
    <PanelHeader>Input</PanelHeader>
    <Group>
      <FormItem top="Фамилия">
        <Input type="text" defaultValue="Петров" />
      </FormItem>
      <FormItem>
        <Input type="text" defaultValue="Иванов" align="center" />
      </FormItem>
      <FormItem>
        <Input type="text" defaultValue="Сидоров" align="right" />
      </FormItem>
      <FormItem top="С иконкой">
        <Input
          type="text"
          defaultValue="Смирнов"
          after={<Icon20User aria-hidden="true" />}
        />
      </FormItem>
      <FormItem top="С IconButton">
        <Input
          getRef={textInput}
          type="text"
          defaultValue="Кузнецов"
          after={
            <IconButton
              hoverMode="opacity"
              aria-label="Очистить поле"
              onClick={clear}
            >
              <Icon16Clear />
            </IconButton>
          }
        />
      </FormItem>
    </Group>
  </Panel>
</View>;









export default App;
