import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

//bridge.send("VKWebAppInit");

import Home from './panels/Home';
import Persik from './panels/Persik';

//alert(jQuery.fn.jquery);
/*
if (bridge){
	alert("ok");
}
*/

<View activePanel="panel">
  <Panel id="panel">
    <PanelHeader>Radio</PanelHeader>
    <Group>
      <FormLayout>
        <FormItem top="Откуда списать">
          <Radio
            name="radio"
            value="1"
            description="Баланс 7 320 ₽"
            defaultChecked
          >
            VK Pay
          </Radio>
          <Radio name="radio" value="2">
            Mastercard **** 1234
          </Radio>
          <Radio name="radio" value="3" description="Заблокирована" disabled>
            Visa **** 4321
          </Radio>
        </FormItem>
      </FormLayout>
    </Group>
  </Panel>
</View>



alert(bridge.send("VKWebAppInit"));

const App = () => {


	bridge.send("VKWebAppInit");
	//alert("");
	//alert("Hello");
	bridge.send("VKWebAppGetClientVersion");

	const [scheme, setScheme] = useState('bright_light')
	const [activePanel, setActivePanel] = useState('home');
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(<ScreenSpinner size='large' />);

	useEffect(() => {
		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				setScheme(data.scheme)
			}
		});

		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
		<ConfigProvider scheme={scheme}>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						<SplitCol>
							<View activePanel={activePanel}>
								<Home id='home' fetchedUser={fetchedUser} go={go} />
								<Persik id='persik' go={go} />
							</View>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
